response.body:
{
  "currencyInterestInfos": [
    {{#each (jsonPath request.body '$.currencies')}}
    {
      "currency": "{{this}}",
      "interestRate": {{randomFloat min=0.1 max=5.0 format='0.00'}},
      "effectiveDate": "{{now offset='0d' format='yyyy-MM-dd'}}"
    }{{#unless @last}},{{/unless}}
    {{/each}}
  ]
}
